class AdaptiveWindowManager:
    def __init__(
        self,
        max_context_length: int = 4096,
        min_window_size: int = 50,
        max_window_size: int = 4096,
        target_efficiency: float = 0.85,
        complexity_threshold: float = 0.7,
        prediction_confidence: float = 0.8,
        adaptation_strategy: str = "dynamic",
    ):
        self.max_context_length = max_context_length
        self.min_window_size = min_window_size
        self.max_window_size = max_window_size
        self.target_efficiency = target_efficiency
        self.complexity_threshold = complexity_threshold
        self.prediction_confidence = prediction_confidence
        self.adaptation_strategy = adaptation_strategy
        
        # State tracking
        self.current_window_size = min_window_size
        self.prompt_complexity_history = []
        self.sequence_length_history = []
        self.efficiency_history = []
    
    def estimate_prompt_complexity(self, token_ids: torch.Tensor, attention_weights: Optional[torch.Tensor] = None) -> float:
        """Estimate the complexity of the input prompt"""
        if len(token_ids.shape) > 1:
            seq_len = token_ids.shape[1]
        else:
            seq_len = len(token_ids)
        
        # Basic complexity factors
        factors = []
        
        # 1. Sequence length factor (normalized)
        length_factor = min(seq_len / self.max_context_length, 1.0)
        factors.append(length_factor)
        
        # 2. Token diversity (vocabulary usage)
        unique_tokens = torch.unique(token_ids).float()
        diversity_factor = len(unique_tokens) / seq_len
        factors.append(diversity_factor)
        
        # 3. Attention pattern complexity (if available)
        if attention_weights is not None:
            attention_entropy = self._calculate_attention_entropy(attention_weights)
            factors.append(attention_entropy)
        else:
            factors.append(0.5)
        
        # 4. Structural complexity (rough estimate based on special tokens)
        special_tokens = torch.sum(token_ids > 30000).float()
        structural_factor = special_tokens / seq_len
        factors.append(structural_factor)
        
        # Weighted combination
        weights = [0.3, 0.25, 0.25, 0.2]
        complexity = sum(w * f for w, f in zip(weights, factors))
        
        return complexity.item()
    
    def _calculate_attention_entropy(self, attention_weights: torch.Tensor) -> float:
        """Calculate entropy of attention patterns"""
        if attention_weights is None:
            return 0.5
        
        # Flatten and normalize
        attn_flat = attention_weights.flatten()
        attn_flat = attn_flat / (attn_flat.sum() + 1e-8)
        
        # Calculate entropy
        entropy = -torch.sum(attn_flat * torch.log(attn_flat + 1e-8))
        max_entropy = math.log(len(attn_flat))
        
        return (entropy / max_entropy).item()
    
    def predict_optimal_window_size(self, current_seq_len: int, prompt_complexity: float, 
                                  historical_data: list[tuple] = None) -> int:
        """Predict optimal window size based on current context"""
        
        if self.adaptation_strategy == "dynamic":
            return self._dynamic_adaptation(current_seq_len, prompt_complexity)
        elif self.adaptation_strategy == "predictive":
            return self._predictive_adaptation(current_seq_len, prompt_complexity, historical_data)
        elif self.adaptation_strategy == "hybrid":
            return self._hybrid_adaptation(current_seq_len, prompt_complexity, historical_data)
        else:
            return self._default_adaptation(current_seq_len, prompt_complexity)
    
    def _dynamic_adaptation(self, current_seq_len: int, prompt_complexity: float) -> int:
        """Dynamic adaptation based on current state"""
        base_size = int(current_seq_len * (1 + prompt_complexity))
        
        # Adjust based on complexity
        if prompt_complexity > self.complexity_threshold:
            window_size = min(base_size * 2, self.max_window_size)
        else:
            window_size = max(base_size, self.min_window_size)
        
        window_size = max(self.min_window_size, min(window_size, self.max_window_size))
        window_size = (window_size + 31) // 64 * 64
        
        return window_size
    
    def _predictive_adaptation(self, current_seq_len: int, prompt_complexity: float, 
                             historical_data: list[tuple]) -> int:
        """Predictive adaptation using historical patterns"""
        if not historical_data or len(historical_data) < 5:
            return self._dynamic_adaptation(current_seq_len, prompt_complexity)
        
        recent_patterns = historical_data[-10:]
        similar_patterns = [pat for pat in recent_patterns 
                          if abs(pat[1] - prompt_complexity) < 0.2]
        
        if similar_patterns:
            optimal_sizes = [pat[2] for pat in similar_patterns]
            predicted_size = int(sum(optimal_sizes) / len(optimal_sizes))
        else:
            predicted_size = self._dynamic_adaptation(current_seq_len, prompt_complexity)
        
        return max(self.min_window_size, min(predicted_size, self.max_window_size))
    
    def _hybrid_adaptation(self, current_seq_len: int, prompt_complexity: float,
                          historical_data: list[tuple]) -> int:
        """Hybrid approach combining dynamic and predictive methods"""
        dynamic_size = self._dynamic_adaptation(current_seq_len, prompt_complexity)
        
        if historical_data and len(historical_data) >= 3:
            predictive_size = self._predictive_adaptation(current_seq_len, prompt_complexity, historical_data)
            hybrid_size = int(0.7 * dynamic_size + 0.3 * predictive_size)
        else:
            hybrid_size = dynamic_size
        
        return hybrid_size
    
    def _default_adaptation(self, current_seq_len: int, prompt_complexity: float) -> int:
        """Default conservative adaptation"""
        if current_seq_len <= self.min_window_size:
            return self.min_window_size
        elif current_seq_len >= self.max_window_size:
            return self.max_window_size
        else:
            scale = (current_seq_len - self.min_window_size) / (self.max_window_size - self.min_window_size)
            window_size = self.min_window_size + scale * (self.max_window_size - self.min_window_size)
            return int(window_size)
    
    def update_adaptive_window(self, token_ids: torch.Tensor, attention_weights: Optional[torch.Tensor] = None,
                             current_efficiency: Optional[float] = None) -> int:
        """Update and return the optimal window size"""
        current_seq_len = token_ids.shape[1] if len(token_ids.shape) > 1 else len(token_ids)
        
        # Estimate prompt complexity
        complexity = self.estimate_prompt_complexity(token_ids, attention_weights)
        self.prompt_complexity_history.append(complexity)
        
        # Predict optimal window size
        historical_data = list(zip(
            self.sequence_length_history[-10:],
            self.prompt_complexity_history[-10:],
            [self.current_window_size] * min(10, len(self.sequence_length_history))
        ))
        
        optimal_window = self.predict_optimal_window_size(
            current_seq_len, complexity, historical_data
        )
        
        # Update current window size with smoothing
        smoothing_factor = 0.3
        self.current_window_size = int(
            smoothing_factor * optimal_window + 
            (1 - smoothing_factor) * self.current_window_size
        )
        
        # Ensure bounds
        self.current_window_size = max(
            self.min_window_size, 
            min(self.current_window_size, self.max_window_size)
        )
        
        # Update history
        self.sequence_length_history.append(current_seq_len)
        if current_efficiency is not None:
            self.efficiency_history.append(current_efficiency)
        
        return self.current_window_size

class DynamicKVCacheManager:
    def __init__(
        self,
        decay_type: str = "exponential",
        importance_metric: str = "attention",
        window_size: int = 2048,
        decay_factor: float = 0.98,
        importance_power: float = 1.2,
        min_decay: float = 0.2,
        enable_importance: bool = True,
        enable_compression: bool = False,
        compression_ratio: float = 0.8,
        # Adaptive window parameters
        enable_adaptive_window: bool = True,  # Disable by default for stability
        max_context_length: int = 4096,
        min_window_size: int = 512,
        max_window_size: int = 4096,
        adaptation_strategy: str = "dynamic"
    ):
        self.window_size = window_size
        self.original_window_size = window_size
        self.decay_factor = decay_factor
        self.importance_power = importance_power
        self.min_decay = min_decay
        self.enable_importance = enable_importance
        self.enable_compression = enable_compression
        self.compression_ratio = compression_ratio
        
        self.prev_attention = None
        self.current_step = 0
        self.current_sequence_length = 0
        
        self.decay_fn = self._get_decay_fn(decay_type)
        self.importance_fn = self._get_importance_fn(importance_metric)
        
        # Adaptive window management - disabled by default
        self.enable_adaptive_window = enable_adaptive_window
        if enable_adaptive_window:
            self.adaptive_manager = AdaptiveWindowManager(
                max_context_length=max_context_length,
                min_window_size=min_window_size,
                max_window_size=max_window_size,
                adaptation_strategy=adaptation_strategy
            )

    def decay_kv_cache(self, keys: torch.Tensor, values: torch.Tensor, 
                      current_step: int, prev_attention: torch.Tensor,
                      token_ids: Optional[torch.Tensor] = None) -> tuple[torch.Tensor, torch.Tensor]:
        """Enhanced decay with proper timing"""
        if keys is None or values is None:
            return keys, values
        
        batch_size, num_heads, seq_len, head_dim = keys.shape
        self.current_sequence_length = seq_len
        self.current_step = current_step
        
        # Update adaptive window size if enabled (less frequently to reduce overhead)
        if self.enable_adaptive_window and token_ids is not None and current_step % 5 == 0:
            self.update_adaptive_window_size(token_ids, prev_attention)
        
        # Only decay tokens that are beyond the recent window
        if seq_len <= self.window_size:
            return keys, values
            
        positions = torch.arange(seq_len, device=keys.device).unsqueeze(0).unsqueeze(0)
        positions = positions.expand(batch_size, num_heads, -1)
        
        position_decay = self.decay_fn(positions, current_step)
        
        if self.enable_importance and prev_attention is not None:
            importance = self.importance_fn(keys, values, prev_attention)
            importance_modulation = importance ** self.importance_power
            combined_decay = position_decay * importance_modulation
        else:
            combined_decay = position_decay
        
        combined_decay = combined_decay.unsqueeze(-1)
        
        # Use in-place operations to save memory
        decayed_keys = keys * combined_decay
        decayed_values = values * combined_decay
        
        return decayed_keys, decayed_values

    def exponential_decay(self, positions: torch.Tensor, current_step: int) -> torch.Tensor:
        """Simplified exponential decay - most effective"""
        decay_start = max(0, current_step - self.window_size)
        relative_positions = positions - decay_start
        decay_factors = self.decay_factor ** torch.clamp(relative_positions, 0, self.window_size)
        decay_factors = torch.clamp(decay_factors, self.min_decay, 1.0)
        return decay_factors

    def attention_importance(self, keys: torch.Tensor, values: torch.Tensor, prev_attention: torch.Tensor) -> torch.Tensor:
        """Simplified importance calculation"""
        if prev_attention is None:
            batch_size, num_heads, seq_len = keys.shape[0], keys.shape[1], keys.shape[2]
            return torch.ones(batch_size, num_heads, seq_len, device=keys.device)
        
        # Use recent attention patterns
        importance = prev_attention.unsqueeze(-1).expand(-1, -1, keys.shape[2])
        importance = importance / (importance.sum(dim=-1, keepdim=True) + 1e-8)
        return importance
        
    def magnitude_importance(self, keys: torch.Tensor, values: torch.Tensor, prev_attention: torch.Tensor) -> torch.Tensor:
        key_norms = torch.norm(keys, dim=-1)
        importance = key_norms / (key_norms.sum(dim=-1, keepdim=True) + 1e-8)
        return importance
    
    def random_importance(self, keys: torch.Tensor, values: torch.Tensor, prev_attention: torch.Tensor) -> torch.Tensor:
        batch_size, num_heads, seq_len = keys.shape[0], keys.shape[1], keys.shape[2]
        importance = torch.rand(batch_size, num_heads, seq_len, device=keys.device)
        importance = importance / (importance.sum(dim=-1, keepdim=True) + 1e-8)
        return importance
    
    def learned_importance(self, keys: torch.Tensor, values: torch.Tensor, prev_attention: torch.Tensor) -> torch.Tensor:
        """Importance based on learned patterns"""
        if prev_attention is None:
            batch_size, num_heads, seq_len = keys.shape[0], keys.shape[1], keys.shape[2]
            return torch.ones(batch_size, num_heads, seq_len, device=keys.device)
        
        # Combine attention with key magnitude
        attention_importance = prev_attention.detach()
        key_magnitude = torch.norm(keys, dim=-1)
        
        # Normalize both
        attention_importance = attention_importance / (attention_importance.sum(dim=-1, keepdim=True) + 1e-8)
        key_magnitude = key_magnitude / (key_magnitude.sum(dim=-1, keepdim=True) + 1e-8)
        
        # Combined importance
        combined_importance = 0.7 * attention_importance + 0.3 * key_magnitude
        combined_importance = combined_importance / (combined_importance.sum(dim=-1, keepdim=True) + 1e-8)
        
        return combined_importance
    
    def update_adaptive_window_size(self, token_ids: torch.Tensor, attention_weights: Optional[torch.Tensor] = None):
        """Update the window size adaptively"""
        if not self.enable_adaptive_window:
            return self.window_size
        
        # Calculate current efficiency
        current_efficiency = self._calculate_current_efficiency()
        
        # Update adaptive window
        new_window_size = self.adaptive_manager.update_adaptive_window(
            token_ids, attention_weights, current_efficiency
        )
        
        # Update the window size
        self.window_size = new_window_size
        return new_window_size
    
    def _calculate_current_efficiency(self) -> float:
        """Calculate current cache efficiency"""
        if self.current_sequence_length == 0:
            return 1.0
        
        efficiency = min(1.0, self.window_size / self.current_sequence_length)
        return efficiency
    
    def decay_kv_cache(self, keys: torch.Tensor, values: torch.Tensor, 
                      current_step: int, prev_attention: torch.Tensor,
                      token_ids: Optional[torch.Tensor] = None) -> tuple[torch.Tensor, torch.Tensor]:
        """Enhanced decay with adaptive window sizing"""
        if keys is None or values is None:
            return keys, values
        
        batch_size, num_heads, seq_len, head_dim = keys.shape
        self.current_sequence_length = seq_len
        self.current_step = current_step
        
        # Update adaptive window size if enabled
        if self.enable_adaptive_window and token_ids is not None:
            self.update_adaptive_window_size(token_ids, prev_attention)
        
        positions = torch.arange(seq_len, device=keys.device).unsqueeze(0).unsqueeze(0)
        positions = positions.expand(batch_size, num_heads, -1)
        
        position_decay = self.decay_fn(positions, current_step)
        
        if self.enable_importance:
            importance = self.importance_fn(keys, values, prev_attention)
            importance_modulation = importance ** self.importance_power
            combined_decay = position_decay * importance_modulation
        else:
            combined_decay = position_decay
        
        combined_decay = combined_decay.unsqueeze(-1)
        
        decayed_keys = keys * combined_decay
        decayed_values = values * combined_decay
        
        return decayed_keys, decayed_values

    def get_cache_stats(self) -> dict[str, any]:
        """Get current cache statistics"""
        stats = {
            'current_step': self.current_step,
            'current_sequence_length': self.current_sequence_length,
            'window_size': self.window_size,
            'enable_adaptive_window': self.enable_adaptive_window,
            'enable_importance': self.enable_importance,
        }
        
        if self.enable_adaptive_window:
            stats.update({
                'adaptive_window_size': self.adaptive_manager.current_window_size,
                'min_window_size': self.adaptive_manager.min_window_size,
                'max_window_size': self.adaptive_manager.max_window_size,
            })
        
        return stats

@use_kernel_forward_from_hub("RMSNorm")
class Qwen3RMSNorm(nn.Module):
    def __init__(self, hidden_size, eps: float = 1e-6) -> None:
        """
        Qwen3RMSNorm is equivalent to T5LayerNorm
        """
        super().__init__()
        self.weight = nn.Parameter(torch.ones(hidden_size))
        self.variance_epsilon = eps

    def forward(self, hidden_states: torch.Tensor) -> torch.Tensor:
        input_dtype = hidden_states.dtype
        hidden_states = hidden_states.to(torch.float32)
        variance = hidden_states.pow(2).mean(-1, keepdim=True)
        hidden_states = hidden_states * torch.rsqrt(variance + self.variance_epsilon)
        return self.weight * hidden_states.to(input_dtype)

    def extra_repr(self):
        return f"{tuple(self.weight.shape)}, eps={self.variance_epsilon}"


class Qwen3MLP(nn.Module):
    def __init__(self, config):
        super().__init__()
        self.config = config
        self.hidden_size = config.hidden_size
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.up_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=False)
        self.act_fn = ACT2FN[config.hidden_act]

    def forward(self, x):
        down_proj = self.down_proj(self.act_fn(self.gate_proj(x)) * self.up_proj(x))
        return down_proj


def rotate_half(x):
    """Rotates half the hidden dims of the input."""
    x1 = x[..., : x.shape[-1] // 2]
    x2 = x[..., x.shape[-1] // 2 :]
    return torch.cat((-x2, x1), dim=-1)


def apply_rotary_pos_emb(q, k, cos, sin, position_ids=None, unsqueeze_dim=1):
    """Applies Rotary Position Embedding to the query and key tensors.

    Args:
        q (`torch.Tensor`): The query tensor.
        k (`torch.Tensor`): The key tensor.
        cos (`torch.Tensor`): The cosine part of the rotary embedding.
        sin (`torch.Tensor`): The sine part of the rotary embedding.
        position_ids (`torch.Tensor`, *optional*):
            Deprecated and unused.
        unsqueeze_dim (`int`, *optional*, defaults to 1):
            The 'unsqueeze_dim' argument specifies the dimension along which to unsqueeze cos[position_ids] and
            sin[position_ids] so that they can be properly broadcasted to the dimensions of q and k. For example, note
            that cos[position_ids] and sin[position_ids] have the shape [batch_size, seq_len, head_dim]. Then, if q and
            k have the shape [batch_size, heads, seq_len, head_dim], then setting unsqueeze_dim=1 makes
            cos[position_ids] and sin[position_ids] broadcastable to the shapes of q and k. Similarly, if q and k have
            the shape [batch_size, seq_len, heads, head_dim], then set unsqueeze_dim=2.
    Returns:
        `tuple(torch.Tensor)` comprising of the query and key tensors rotated using the Rotary Position Embedding.
    """
    cos = cos.unsqueeze(unsqueeze_dim)
    sin = sin.unsqueeze(unsqueeze_dim)
    q_embed = (q * cos) + (rotate_half(q) * sin)
    k_embed = (k * cos) + (rotate_half(k) * sin)
    return q_embed, k_embed


def repeat_kv(hidden_states: torch.Tensor, n_rep: int) -> torch.Tensor:
    """
    This is the equivalent of torch.repeat_interleave(x, dim=1, repeats=n_rep). The hidden states go from (batch,
    num_key_value_heads, seqlen, head_dim) to (batch, num_attention_heads, seqlen, head_dim)
    """
    batch, num_key_value_heads, slen, head_dim = hidden_states.shape
    if n_rep == 1:
        return hidden_states
    hidden_states = hidden_states[:, :, None, :, :].expand(batch, num_key_value_heads, n_rep, slen, head_dim)
    return hidden_states.reshape(batch, num_key_value_heads * n_rep, slen, head_dim)


def eager_attention_forward(
    module: nn.Module,
    query: torch.Tensor,
    key: torch.Tensor,
    value: torch.Tensor,
    attention_mask: Optional[torch.Tensor],
    scaling: float,
    dropout: float = 0.0,
    **kwargs: Unpack[TransformersKwargs],
):
    key_states = repeat_kv(key, module.num_key_value_groups)
    value_states = repeat_kv(value, module.num_key_value_groups)

    attn_weights = torch.matmul(query, key_states.transpose(2, 3)) * scaling
    if attention_mask is not None:
        causal_mask = attention_mask[:, :, :, : key_states.shape[-2]]
        attn_weights = attn_weights + causal_mask

    attn_weights = nn.functional.softmax(attn_weights, dim=-1, dtype=torch.float32).to(query.dtype)
    attn_weights = nn.functional.dropout(attn_weights, p=dropout, training=module.training)
    attn_output = torch.matmul(attn_weights, value_states)
    attn_output = attn_output.transpose(1, 2).contiguous()

    return attn_output, attn_weights


class Qwen3Attention(nn.Module):
    """Multi-headed attention from 'Attention Is All You Need' paper"""

    def __init__(self, config: Qwen3ShatteredShards4BConfig, layer_idx: int):
        super().__init__()
        self.config = config
        self.layer_idx = layer_idx
        self.head_dim = getattr(config, "head_dim", config.hidden_size // config.num_attention_heads)
        self.num_key_value_groups = config.num_attention_heads // config.num_key_value_heads
        self.scaling = self.head_dim**-0.5
        self.attention_dropout = config.attention_dropout
        self.is_causal = True

        self.q_proj = nn.Linear(
            config.hidden_size, config.num_attention_heads * self.head_dim, bias=config.attention_bias
        )
        self.k_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.v_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.o_proj = nn.Linear(
            config.num_attention_heads * self.head_dim, config.hidden_size, bias=config.attention_bias
        )
        self.q_norm = Qwen3RMSNorm(self.head_dim, eps=config.rms_norm_eps)
        self.k_norm = Qwen3RMSNorm(self.head_dim, eps=config.rms_norm_eps)
        self.sliding_window = config.sliding_window if config.layer_types[layer_idx] == "sliding_attention" else None

        # Dynamic KV Cache Manager
        self.kv_cache_manager = None
        if hasattr(config, 'enable_dynamic_kv_cache') and config.enable_dynamic_kv_cache:
            kv_config = getattr(config, 'dynamic_kv_config', {})
            self.kv_cache_manager = DynamicKVCacheManager(**kv_config)

    @deprecate_kwarg("past_key_value", new_name="past_key_values", version="4.58")
    def forward(
        self,
        hidden_states: torch.Tensor,
        position_embeddings: tuple[torch.Tensor, torch.Tensor],
        attention_mask: Optional[torch.Tensor],
        past_key_values: Optional[Cache] = None,
        cache_position: Optional[torch.LongTensor] = None,
        **kwargs: Unpack[FlashAttentionKwargs],
    ) -> tuple[torch.Tensor, Optional[torch.Tensor]]:
        input_shape = hidden_states.shape[:-1]
        hidden_shape = (*input_shape, -1, self.head_dim)

        query_states = self.q_norm(self.q_proj(hidden_states).view(hidden_shape)).transpose(1, 2)
        key_states = self.k_norm(self.k_proj(hidden_states).view(hidden_shape)).transpose(1, 2)
        value_states = self.v_proj(hidden_states).view(hidden_shape).transpose(1, 2)

        cos, sin = position_embeddings
        query_states, key_states = apply_rotary_pos_emb(query_states, key_states, cos, sin)

        # ðŸš€ CRITICAL FIX: Apply decay BEFORE attention computation and cache update
        current_step = 0
        if past_key_values is not None:
            current_step = past_key_values.get_seq_length() if hasattr(past_key_values, 'get_seq_length') else 0
            
            # Apply decay to existing cache BEFORE using it for attention
            if (self.kv_cache_manager is not None and 
                past_key_values is not None and
                self.layer_idx in past_key_values.key_cache):
                
                current_keys = past_key_values.key_cache[self.layer_idx]
                current_values = past_key_values.value_cache[self.layer_idx]
                
                if current_keys is not None:
                    # ðŸŽ¯ DECAY FIRST, then use for attention
                    decayed_keys, decayed_values = self.kv_cache_manager.decay_kv_cache(
                        current_keys, current_values, 
                        self.kv_cache_manager.current_step,
                        self.kv_cache_manager.prev_attention
                    )
                    past_key_values.key_cache[self.layer_idx] = decayed_keys
                    past_key_values.value_cache[self.layer_idx] = decayed_values

        # Now update cache with current states (AFTER decay but BEFORE attention)
        if past_key_values is not None:
            cache_kwargs = {"sin": sin, "cos": cos, "cache_position": cache_position}
            key_states, value_states = past_key_values.update(key_states, value_states, self.layer_idx, cache_kwargs)

        attention_interface: Callable = eager_attention_forward
        if self.config._attn_implementation != "eager":
            attention_interface = ALL_ATTENTION_FUNCTIONS[self.config._attn_implementation]

        # Compute attention using the DECAYED cache + new tokens
        attn_output, attn_weights = attention_interface(
            self,
            query_states,
            key_states,
            value_states,
            attention_mask,
            dropout=0.0 if not self.training else self.attention_dropout,
            scaling=self.scaling,
            sliding_window=self.sliding_window,
            **kwargs,
        )

        # Store attention for NEXT step's decay (current step is already processed)
        if self.kv_cache_manager is not None and attn_weights is not None:
            # Store compressed attention for next step
            attention_summary = attn_weights.mean(dim=-1).detach()  # [batch, heads]
            self.kv_cache_manager.prev_attention = attention_summary
            
            # Update step counter for next iteration
            self.kv_cache_manager.current_step = current_step + 1

        attn_output = attn_output.reshape(*input_shape, -1).contiguous()
        attn_output = self.o_proj(attn_output)
        return attn_output, attn_weights


class Qwen3DecoderLayer(GradientCheckpointingLayer):
    def __init__(self, config: Qwen3ShatteredShards4BConfig, layer_idx: int):
        super().__init__()
        self.hidden_size = config.hidden_size

        self.self_attn = Qwen3Attention(config=config, layer_idx=layer_idx)

        self.mlp = Qwen3MLP(config)
        self.input_layernorm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.post_attention_layernorm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.attention_type = config.layer_types[layer_idx]

    @deprecate_kwarg("past_key_value", new_name="past_key_values", version="4.58")
    def forward(
        self,
        hidden_states: torch.Tensor,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Cache] = None,
        use_cache: Optional[bool] = False,
        cache_position: Optional[torch.LongTensor] = None,
        position_embeddings: Optional[tuple[torch.Tensor, torch.Tensor]] = None,  # necessary, but kept here for BC
        **kwargs: Unpack[TransformersKwargs],
    ) -> torch.Tensor:
        residual = hidden_states
        hidden_states = self.input_layernorm(hidden_states)
        # Self Attention
        hidden_states, _ = self.self_attn(
            hidden_states=hidden_states,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            use_cache=use_cache,
            cache_position=cache_position,
            position_embeddings=position_embeddings,
            **kwargs,
        )
        hidden_states = residual + hidden_states

        # Fully Connected
        residual = hidden_states
        hidden_states = self.post_attention_layernorm(hidden_states)
        hidden_states = self.mlp(hidden_states)
        hidden_states = residual + hidden_states
        return hidden_states


@auto_docstring
class Qwen3PreTrainedModel(PreTrainedModel):
    config: Qwen3ShatteredShards4BConfig
    base_model_prefix = "model"
    supports_gradient_checkpointing = True
    _no_split_modules = ["Qwen3DecoderLayer"]
